// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./ConditionalTokens.sol";

/**
 * @title AdminOracle
 * @notice Oracle for resolving UFC markets with payout arrays
 * @dev Supports corrections, challenge periods, and role-based access
 */
contract AdminOracle is Ownable, AccessControl {

    bytes32 public constant RESOLVER_ROLE = keccak256("RESOLVER_ROLE");

    ConditionalTokens public conditionalTokens;

    struct ResolutionProposal {
        bytes32 conditionId;
        uint256[] payoutNumerators;  // Payout weights per outcome
        uint256 proposedAt;
        bool finalized;
        string resultSource;
    }

    uint256 public challengePeriod = 0; // 0 for instant resolution

    mapping(bytes32 => ResolutionProposal) public proposals;

    event ResolutionProposed(
        bytes32 indexed conditionId,
        uint256[] payoutNumerators,
        string resultSource
    );

    event ResolutionFinalized(bytes32 indexed conditionId);
    event ResolutionOverwritten(bytes32 indexed conditionId, string reason);
    event ChallengePeriodUpdated(uint256 newPeriod);

    constructor(address _conditionalTokens) Ownable(msg.sender) {
        conditionalTokens = ConditionalTokens(payable(_conditionalTokens));
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(RESOLVER_ROLE, msg.sender);
    }

    /**
     * @notice Propose resolution with payout array
     * @param conditionId Market to resolve
     * @param payoutNumerators Payout weights (e.g., [1,0] for binary outcome 0 wins)
     * @param resultSource Official UFC result URL
     */
    function proposeResolution(
        bytes32 conditionId,
        uint256[] calldata payoutNumerators,
        string calldata resultSource
    ) external onlyRole(RESOLVER_ROLE) {
        ConditionalTokens.Market memory market = conditionalTokens.getMarket(conditionId);
        require(market.outcomeSlotCount > 0, "Market does not exist");
        require(!market.resolved, "Already resolved on chain");
        require(payoutNumerators.length == market.outcomeSlotCount, "Invalid payout length");

        // Validate at least one non-zero payout
        uint256 sum = 0;
        for (uint256 i = 0; i < payoutNumerators.length; i++) {
            sum += payoutNumerators[i];
        }
        require(sum > 0, "Must have winning outcome");

        // Allow overwrite if not finalized
        if (proposals[conditionId].proposedAt > 0 && !proposals[conditionId].finalized) {
            emit ResolutionOverwritten(conditionId, "New proposal");
        }

        proposals[conditionId] = ResolutionProposal({
            conditionId: conditionId,
            payoutNumerators: payoutNumerators,
            proposedAt: block.timestamp,
            finalized: false,
            resultSource: resultSource
        });

        emit ResolutionProposed(conditionId, payoutNumerators, resultSource);

        if (challengePeriod == 0) {
            _finalizeResolution(conditionId);
        }
    }

    /**
     * @notice Finalize after challenge period
     */
    function finalizeResolution(bytes32 conditionId) external {
        ResolutionProposal storage proposal = proposals[conditionId];
        require(proposal.proposedAt > 0, "No proposal");
        require(!proposal.finalized, "Already finalized");
        require(block.timestamp >= proposal.proposedAt + challengePeriod, "Challenge period active");

        _finalizeResolution(conditionId);
    }

    function _finalizeResolution(bytes32 conditionId) internal {
        ResolutionProposal storage proposal = proposals[conditionId];
        proposal.finalized = true;

        conditionalTokens.resolveMarket(conditionId, proposal.payoutNumerators);

        emit ResolutionFinalized(conditionId);
    }

    /**
     * @notice Emergency resolution by owner
     */
    function emergencyResolve(
        bytes32 conditionId,
        uint256[] calldata payoutNumerators,
        string calldata resultSource
    ) external onlyOwner {
        ConditionalTokens.Market memory market = conditionalTokens.getMarket(conditionId);
        require(!market.resolved, "Already resolved");
        require(payoutNumerators.length == market.outcomeSlotCount, "Invalid payout length");

        proposals[conditionId] = ResolutionProposal({
            conditionId: conditionId,
            payoutNumerators: payoutNumerators,
            proposedAt: block.timestamp,
            finalized: true,
            resultSource: resultSource
        });

        conditionalTokens.resolveMarket(conditionId, payoutNumerators);

        emit ResolutionProposed(conditionId, payoutNumerators, resultSource);
        emit ResolutionFinalized(conditionId);
    }

    function setChallengePeriod(uint256 _period) external onlyOwner {
        challengePeriod = _period;
        emit ChallengePeriodUpdated(_period);
    }

    function addResolver(address resolver) external onlyOwner {
        grantRole(RESOLVER_ROLE, resolver);
    }

    function removeResolver(address resolver) external onlyOwner {
        revokeRole(RESOLVER_ROLE, resolver);
    }

    function getProposal(bytes32 conditionId) external view returns (ResolutionProposal memory) {
        return proposals[conditionId];
    }

    function getPayoutNumerators(bytes32 conditionId) external view returns (uint256[] memory) {
        return proposals[conditionId].payoutNumerators;
    }
}

contracts/ConditionalTokens.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ConditionalTokens
 * @notice Core conditional token framework for Octagon Predict
 * @dev Adapted from Gnosis Conditional Tokens for VeChainThor
 * 
 * Binary outcome markets: Each market has N outcome tokens
 * Users deposit collateral to mint full sets of outcome tokens
 * After resolution, winning tokens can be redeemed for collateral
 * 
 * Key design: 1 unit of collateral = 1 unit of EACH outcome token
 * After resolution, 1 winning token = 1 unit of collateral (minus fees from losers)
 */
contract ConditionalTokens is ERC1155, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct Market {
        bytes32 conditionId;
        address collateralToken;    // address(0) for native VET
        uint256 outcomeSlotCount;
        uint256 totalCollateral;    // Total collateral in escrow
        uint256[] payoutNumerators; // Payout per outcome (set on resolution)
        uint256 payoutDenominator;  // Sum of numerators (for proportional payouts)
        bool resolved;
        string eventUri;
    }

    mapping(bytes32 => Market) public markets;

    // Track redemptions: conditionId => user => outcomeIndex => redeemed amount
    mapping(bytes32 => mapping(address => mapping(uint256 => uint256))) public redemptions;

    address public oracle;

    event MarketCreated(
        bytes32 indexed conditionId,
        address indexed collateralToken,
        uint256 outcomeSlotCount,
        string eventUri
    );

    event PositionSplit(
        bytes32 indexed conditionId,
        address indexed stakeholder,
        uint256 amount
    );

    event PositionMerged(
        bytes32 indexed conditionId,
        address indexed stakeholder,
        uint256 amount
    );

    event MarketResolved(
        bytes32 indexed conditionId,
        uint256[] payoutNumerators
    );

    event PayoutRedeemed(
        bytes32 indexed conditionId,
        address indexed redeemer,
        uint256[] amounts,
        uint256 payout
    );

    constructor(address _oracle) ERC1155("") Ownable(msg.sender) {
        oracle = _oracle;
    }

    modifier onlyOracle() {
        require(msg.sender == oracle, "Only oracle can call");
        _;
    }

    function createMarket(
        bytes32 conditionId,
        address collateralToken,
        uint256 outcomeSlotCount,
        string calldata eventUri
    ) external onlyOwner {
        require(markets[conditionId].outcomeSlotCount == 0, "Market already exists");
        require(outcomeSlotCount >= 2 && outcomeSlotCount <= 256, "Invalid outcome count");

        markets[conditionId] = Market({
            conditionId: conditionId,
            collateralToken: collateralToken,
            outcomeSlotCount: outcomeSlotCount,
            totalCollateral: 0,
            payoutNumerators: new uint256[](outcomeSlotCount),
            payoutDenominator: 0,
            resolved: false,
            eventUri: eventUri
        });

        emit MarketCreated(conditionId, collateralToken, outcomeSlotCount, eventUri);
    }

    /**
     * @notice Split collateral into complete sets of outcome tokens
     * @dev 1 collateral = 1 of each outcome token (complete set)
     */
    function splitPosition(
        bytes32 conditionId,
        uint256 amount
    ) external payable nonReentrant {
        Market storage market = markets[conditionId];
        require(market.outcomeSlotCount > 0, "Market does not exist");
        require(!market.resolved, "Market already resolved");
        require(amount > 0, "Amount must be positive");

        // Handle collateral deposit
        if (market.collateralToken == address(0)) {
            require(msg.value == amount, "Incorrect VET amount");
        } else {
            require(msg.value == 0, "Do not send VET with ERC20");
            IERC20(market.collateralToken).safeTransferFrom(msg.sender, address(this), amount);
        }

        market.totalCollateral += amount;

        // Mint one of each outcome token
        uint256[] memory ids = new uint256[](market.outcomeSlotCount);
        uint256[] memory amounts = new uint256[](market.outcomeSlotCount);

        for (uint256 i = 0; i < market.outcomeSlotCount; i++) {
            ids[i] = getTokenId(conditionId, i);
            amounts[i] = amount;
        }

        _mintBatch(msg.sender, ids, amounts, "");

        emit PositionSplit(conditionId, msg.sender, amount);
    }

    /**
     * @notice Merge complete sets back into collateral (before resolution)
     * @dev Must burn equal amounts of ALL outcome tokens
     */
    function mergePositions(
        bytes32 conditionId,
        uint256 amount
    ) external nonReentrant {
        Market storage market = markets[conditionId];
        require(market.outcomeSlotCount > 0, "Market does not exist");
        require(!market.resolved, "Market already resolved");
        require(amount > 0, "Amount must be positive");

        // Verify and burn all outcome tokens
        uint256[] memory ids = new uint256[](market.outcomeSlotCount);
        uint256[] memory amounts = new uint256[](market.outcomeSlotCount);

        for (uint256 i = 0; i < market.outcomeSlotCount; i++) {
            ids[i] = getTokenId(conditionId, i);
            amounts[i] = amount;
            require(balanceOf(msg.sender, ids[i]) >= amount, "Insufficient balance");
        }

        _burnBatch(msg.sender, ids, amounts);
        market.totalCollateral -= amount;

        // Return collateral using call for VeChain compatibility
        if (market.collateralToken == address(0)) {
            (bool success, ) = payable(msg.sender).call{value: amount}("");
            require(success, "VET transfer failed");
        } else {
            IERC20(market.collateralToken).safeTransfer(msg.sender, amount);
        }

        emit PositionMerged(conditionId, msg.sender, amount);
    }

    /**
     * @notice Resolve market with payout distribution
     * @param conditionId The market to resolve
     * @param payoutNumerators Payout weight per outcome (e.g., [1,0] for binary, outcome 0 wins)
     */
    function resolveMarket(
        bytes32 conditionId,
        uint256[] calldata payoutNumerators
    ) external onlyOracle {
        Market storage market = markets[conditionId];
        require(market.outcomeSlotCount > 0, "Market does not exist");
        require(!market.resolved, "Market already resolved");
        require(payoutNumerators.length == market.outcomeSlotCount, "Invalid payout length");

        uint256 denominator = 0;
        for (uint256 i = 0; i < payoutNumerators.length; i++) {
            market.payoutNumerators[i] = payoutNumerators[i];
            denominator += payoutNumerators[i];
        }

        require(denominator > 0, "Invalid payout - no winner");
        market.payoutDenominator = denominator;
        market.resolved = true;

        emit MarketResolved(conditionId, payoutNumerators);
    }

    /**
     * @notice Redeem outcome tokens for collateral payout
     * @dev Can redeem any tokens with non-zero payout numerator
     */
    function redeemPositions(
        bytes32 conditionId,
        uint256[] calldata amounts
    ) external nonReentrant {
        Market storage market = markets[conditionId];
        require(market.resolved, "Market not resolved");
        require(amounts.length == market.outcomeSlotCount, "Invalid amounts length");

        uint256 totalPayout = 0;

        for (uint256 i = 0; i < market.outcomeSlotCount; i++) {
            if (amounts[i] == 0) continue;

            uint256 tokenId = getTokenId(conditionId, i);
            uint256 balance = balanceOf(msg.sender, tokenId);
            require(balance >= amounts[i], "Insufficient balance");

            // Calculate payout for this outcome
            if (market.payoutNumerators[i] > 0) {
                uint256 payout = (amounts[i] * market.payoutNumerators[i]) / market.payoutDenominator;
                totalPayout += payout;
            }

            // Burn tokens (even losing tokens can be burned)
            _burn(msg.sender, tokenId, amounts[i]);
            redemptions[conditionId][msg.sender][i] += amounts[i];
        }

        require(totalPayout > 0, "No payout available");
        require(totalPayout <= market.totalCollateral, "Insufficient collateral");

        market.totalCollateral -= totalPayout;

        // Transfer payout using call for VeChain compatibility
        if (market.collateralToken == address(0)) {
            (bool success, ) = payable(msg.sender).call{value: totalPayout}("");
            require(success, "VET transfer failed");
        } else {
            IERC20(market.collateralToken).safeTransfer(msg.sender, totalPayout);
        }

        emit PayoutRedeemed(conditionId, msg.sender, amounts, totalPayout);
    }

    function getTokenId(bytes32 conditionId, uint256 outcomeIndex) public pure returns (uint256) {
        return uint256(keccak256(abi.encodePacked(conditionId, outcomeIndex)));
    }

    function getMarket(bytes32 conditionId) external view returns (Market memory) {
        return markets[conditionId];
    }

    function getPayoutNumerators(bytes32 conditionId) external view returns (uint256[] memory) {
        return markets[conditionId].payoutNumerators;
    }

    function setOracle(address _oracle) external onlyOwner {
        oracle = _oracle;
    }

    receive() external payable {}
}

contracts/Exchange.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./ConditionalTokens.sol";

/**
 * @title Exchange
 * @notice Order settlement for Octagon Predict with user escrow balances
 * @dev Off-chain matching + on-chain settlement with proper balance tracking
 * 
 * Flow:
 * 1. Users deposit collateral to their Exchange balance
 * 2. Users sign EIP-712 orders off-chain
 * 3. Matching engine pairs orders
 * 4. Operator settles matched trades (transfers outcome tokens, adjusts balances)
 */
contract Exchange is Ownable, ReentrancyGuard {
    using ECDSA for bytes32;
    using SafeERC20 for IERC20;

    ConditionalTokens public conditionalTokens;

    // User balances per collateral token
    // user => collateralToken => balance
    mapping(address => mapping(address => uint256)) public balances;

    // Order structure
    struct Order {
        address maker;
        bytes32 conditionId;
        uint256 outcomeIndex;
        bool isBuy;             // true = buying outcome tokens, false = selling
        uint256 price;          // Price in 1e18 units (0.5e18 = 50%)
        uint256 amount;         // Amount of outcome tokens
        uint256 nonce;
        uint256 expiry;
    }

    struct Fill {
        Order makerOrder;
        bytes makerSignature;
        Order takerOrder;
        bytes takerSignature;
        uint256 fillAmount;
    }

    mapping(address => uint256) public userNonces;
    mapping(bytes32 => uint256) public orderFills;
    mapping(bytes32 => bool) public cancelledOrders;
    mapping(address => bool) public operators;

    uint256 public tradingFeeBps = 100; // 1%
    address public feeRecipient;

    bytes32 public immutable DOMAIN_SEPARATOR;
    bytes32 public constant ORDER_TYPEHASH = keccak256(
        "Order(address maker,bytes32 conditionId,uint256 outcomeIndex,bool isBuy,uint256 price,uint256 amount,uint256 nonce,uint256 expiry)"
    );

    uint256 constant PRICE_PRECISION = 1e18;

    event Deposit(address indexed user, address indexed token, uint256 amount);
    event Withdrawal(address indexed user, address indexed token, uint256 amount);
    event OrderFilled(
        bytes32 indexed orderHash,
        address indexed maker,
        address indexed taker,
        bytes32 conditionId,
        uint256 fillAmount,
        uint256 price
    );
    event OrderCancelled(bytes32 indexed orderHash, address indexed maker);

    constructor(address _conditionalTokens, address _feeRecipient) Ownable(msg.sender) {
        conditionalTokens = ConditionalTokens(payable(_conditionalTokens));
        feeRecipient = _feeRecipient;
        operators[msg.sender] = true;

        DOMAIN_SEPARATOR = keccak256(abi.encode(
            keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
            keccak256("OctagonPredict"),
            keccak256("1"),
            block.chainid,
            address(this)
        ));
    }

    modifier onlyOperator() {
        require(operators[msg.sender], "Not an operator");
        _;
    }

    /**
     * @notice Deposit collateral (VET or ERC20) into exchange balance
     */
    function deposit(address token, uint256 amount) external payable nonReentrant {
        if (token == address(0)) {
            require(msg.value == amount, "Incorrect VET amount");
        } else {
            require(msg.value == 0, "Do not send VET");
            IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        }

        balances[msg.sender][token] += amount;
        emit Deposit(msg.sender, token, amount);
    }

    /**
     * @notice Withdraw collateral from exchange balance
     */
    function withdraw(address token, uint256 amount) external nonReentrant {
        require(balances[msg.sender][token] >= amount, "Insufficient balance");
        balances[msg.sender][token] -= amount;

        if (token == address(0)) {
            (bool success, ) = payable(msg.sender).call{value: amount}("");
            require(success, "VET transfer failed");
        } else {
            IERC20(token).safeTransfer(msg.sender, amount);
        }

        emit Withdrawal(msg.sender, token, amount);
    }

    /**
     * @notice Batch settle matched orders
     */
    function settleBatch(Fill[] calldata fills) external onlyOperator nonReentrant {
        for (uint256 i = 0; i < fills.length; i++) {
            _settleFill(fills[i]);
        }
    }

    function _settleFill(Fill calldata fill) internal {
        Order calldata maker = fill.makerOrder;
        Order calldata taker = fill.takerOrder;

        // Validate match
        require(maker.conditionId == taker.conditionId, "Condition mismatch");
        require(maker.outcomeIndex == taker.outcomeIndex, "Outcome mismatch");
        require(maker.isBuy != taker.isBuy, "Same side");
        require(block.timestamp <= maker.expiry && block.timestamp <= taker.expiry, "Order expired");
        require(maker.nonce >= userNonces[maker.maker], "Maker nonce invalid");
        require(taker.nonce >= userNonces[taker.maker], "Taker nonce invalid");

        // Verify signatures
        bytes32 makerHash = getOrderHash(maker);
        bytes32 takerHash = getOrderHash(taker);

        require(!cancelledOrders[makerHash] && !cancelledOrders[takerHash], "Order cancelled");
        require(_verifySignature(makerHash, fill.makerSignature, maker.maker), "Invalid maker sig");
        require(_verifySignature(takerHash, fill.takerSignature, taker.maker), "Invalid taker sig");

        // Check fill amounts
        uint256 makerRemaining = maker.amount - orderFills[makerHash];
        uint256 takerRemaining = taker.amount - orderFills[takerHash];
        uint256 fillAmount = fill.fillAmount;

        require(fillAmount > 0 && fillAmount <= makerRemaining && fillAmount <= takerRemaining, "Invalid fill");

        // Price validation: execution at maker's price
        // Buyer pays: fillAmount * price / PRICE_PRECISION
        // Seller receives: fillAmount * price / PRICE_PRECISION - fees

        uint256 executionPrice = maker.price;
        if (maker.isBuy) {
            require(taker.price <= maker.price, "Price mismatch");
        } else {
            require(taker.price >= maker.price, "Price mismatch");
            executionPrice = taker.price;
        }

        uint256 collateralAmount = (fillAmount * executionPrice) / PRICE_PRECISION;

        // Get collateral token from market
        ConditionalTokens.Market memory market = conditionalTokens.getMarket(maker.conditionId);
        address collateralToken = market.collateralToken;

        address buyer = maker.isBuy ? maker.maker : taker.maker;
        address seller = maker.isBuy ? taker.maker : maker.maker;

        // Verify balances
        require(balances[buyer][collateralToken] >= collateralAmount, "Buyer insufficient balance");

        uint256 tokenId = conditionalTokens.getTokenId(maker.conditionId, maker.outcomeIndex);
        require(conditionalTokens.balanceOf(seller, tokenId) >= fillAmount, "Seller insufficient tokens");

        // Execute transfers
        balances[buyer][collateralToken] -= collateralAmount;

        uint256 fee = (collateralAmount * tradingFeeBps) / 10000;
        balances[seller][collateralToken] += (collateralAmount - fee);
        if (fee > 0) {
            balances[feeRecipient][collateralToken] += fee;
        }

        // Transfer outcome tokens (requires approval)
        conditionalTokens.safeTransferFrom(seller, buyer, tokenId, fillAmount, "");

        // Update fills
        orderFills[makerHash] += fillAmount;
        orderFills[takerHash] += fillAmount;

        emit OrderFilled(makerHash, maker.maker, taker.maker, maker.conditionId, fillAmount, executionPrice);
    }

    function cancelOrder(Order calldata order) external {
        require(msg.sender == order.maker, "Not maker");
        bytes32 orderHash = getOrderHash(order);
        cancelledOrders[orderHash] = true;
        emit OrderCancelled(orderHash, msg.sender);
    }

    function incrementNonce() external {
        userNonces[msg.sender]++;
    }

    function getOrderHash(Order calldata order) public view returns (bytes32) {
        bytes32 structHash = keccak256(abi.encode(
            ORDER_TYPEHASH,
            order.maker,
            order.conditionId,
            order.outcomeIndex,
            order.isBuy,
            order.price,
            order.amount,
            order.nonce,
            order.expiry
        ));
        return keccak256(abi.encodePacked("\x19\x01", DOMAIN_SEPARATOR, structHash));
    }

    function _verifySignature(bytes32 hash, bytes calldata signature, address signer) internal pure returns (bool) {
        return hash.recover(signature) == signer;
    }

    function setOperator(address operator, bool approved) external onlyOwner {
        operators[operator] = approved;
    }

    function setTradingFee(uint256 _feeBps) external onlyOwner {
        require(_feeBps <= 500, "Fee too high");
        tradingFeeBps = _feeBps;
    }

    function setFeeRecipient(address _feeRecipient) external onlyOwner {
        feeRecipient = _feeRecipient;
    }

    receive() external payable {}
}

contracts/MarketFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./ConditionalTokens.sol";

/**
 * @title MarketFactory
 * @notice Factory for creating UFC prediction markets on Octagon Predict
 * @dev Deterministic condition IDs, outcome metadata storage
 */
contract MarketFactory is Ownable {

    ConditionalTokens public conditionalTokens;

    enum MarketType {
        WINNER,
        METHOD_OF_VICTORY,
        GOES_DISTANCE,
        ROUND_PROP,
        EVENT_PROP
    }

    struct UFCMarket {
        bytes32 conditionId;
        string eventName;
        string fighterA;
        string fighterB;
        MarketType marketType;
        uint256 eventDate;
        bool active;
        string[] outcomeLabels;     // Human-readable outcome names
        address collateralToken;
    }

    bytes32[] public allMarkets;
    mapping(bytes32 => UFCMarket) public ufcMarkets;
    mapping(address => bool) public approvedCollateral;

    // Nonce for deterministic but unique IDs
    uint256 private marketNonce;

    event UFCMarketCreated(
        bytes32 indexed conditionId,
        string eventName,
        string fighterA,
        string fighterB,
        MarketType marketType,
        string[] outcomeLabels
    );

    event CollateralApproved(address indexed token, bool approved);
    event MarketDeactivated(bytes32 indexed conditionId);

    constructor(address _conditionalTokens) Ownable(msg.sender) {
        conditionalTokens = ConditionalTokens(payable(_conditionalTokens));
        approvedCollateral[address(0)] = true; // Native VET
    }

    /**
     * @notice Generate deterministic condition ID
     */
    function generateConditionId(
        string calldata eventName,
        string calldata fighterA,
        string calldata fighterB,
        MarketType marketType
    ) public view returns (bytes32) {
        return keccak256(abi.encodePacked(
            eventName,
            fighterA,
            fighterB,
            uint8(marketType),
            marketNonce
        ));
    }

    /**
     * @notice Create a winner market (binary: Fighter A vs Fighter B)
     */
    function createWinnerMarket(
        string calldata eventName,
        string calldata fighterA,
        string calldata fighterB,
        uint256 eventDate,
        address collateralToken,
        string calldata eventUri
    ) external onlyOwner returns (bytes32) {
        require(approvedCollateral[collateralToken], "Collateral not approved");
        require(eventDate > block.timestamp, "Event must be in future");

        bytes32 conditionId = generateConditionId(eventName, fighterA, fighterB, MarketType.WINNER);
        require(ufcMarkets[conditionId].conditionId == bytes32(0), "Market already exists");

        marketNonce++;

        string[] memory labels = new string[](2);
        labels[0] = string(abi.encodePacked(fighterA, " wins"));
        labels[1] = string(abi.encodePacked(fighterB, " wins"));

        conditionalTokens.createMarket(conditionId, collateralToken, 2, eventUri);

        ufcMarkets[conditionId] = UFCMarket({
            conditionId: conditionId,
            eventName: eventName,
            fighterA: fighterA,
            fighterB: fighterB,
            marketType: MarketType.WINNER,
            eventDate: eventDate,
            active: true,
            outcomeLabels: labels,
            collateralToken: collateralToken
        });

        allMarkets.push(conditionId);

        emit UFCMarketCreated(conditionId, eventName, fighterA, fighterB, MarketType.WINNER, labels);

        return conditionId;
    }

    /**
     * @notice Create a method of victory market
     * Outcomes: [A by KO/TKO, A by Sub, A by Dec, B by KO/TKO, B by Sub, B by Dec]
     */
    function createMethodMarket(
        string calldata eventName,
        string calldata fighterA,
        string calldata fighterB,
        uint256 eventDate,
        address collateralToken,
        string calldata eventUri
    ) external onlyOwner returns (bytes32) {
        require(approvedCollateral[collateralToken], "Collateral not approved");
        require(eventDate > block.timestamp, "Event must be in future");

        bytes32 conditionId = generateConditionId(eventName, fighterA, fighterB, MarketType.METHOD_OF_VICTORY);
        require(ufcMarkets[conditionId].conditionId == bytes32(0), "Market already exists");

        marketNonce++;

        string[] memory labels = new string[](6);
        labels[0] = string(abi.encodePacked(fighterA, " by KO/TKO"));
        labels[1] = string(abi.encodePacked(fighterA, " by Submission"));
        labels[2] = string(abi.encodePacked(fighterA, " by Decision"));
        labels[3] = string(abi.encodePacked(fighterB, " by KO/TKO"));
        labels[4] = string(abi.encodePacked(fighterB, " by Submission"));
        labels[5] = string(abi.encodePacked(fighterB, " by Decision"));

        conditionalTokens.createMarket(conditionId, collateralToken, 6, eventUri);

        ufcMarkets[conditionId] = UFCMarket({
            conditionId: conditionId,
            eventName: eventName,
            fighterA: fighterA,
            fighterB: fighterB,
            marketType: MarketType.METHOD_OF_VICTORY,
            eventDate: eventDate,
            active: true,
            outcomeLabels: labels,
            collateralToken: collateralToken
        });

        allMarkets.push(conditionId);

        emit UFCMarketCreated(conditionId, eventName, fighterA, fighterB, MarketType.METHOD_OF_VICTORY, labels);

        return conditionId;
    }

    /**
     * @notice Create a "goes the distance" market
     * Outcomes: [Yes (decision), No (finish)]
     */
    function createGoesDistanceMarket(
        string calldata eventName,
        string calldata fighterA,
        string calldata fighterB,
        uint256 eventDate,
        address collateralToken,
        string calldata eventUri
    ) external onlyOwner returns (bytes32) {
        require(approvedCollateral[collateralToken], "Collateral not approved");
        require(eventDate > block.timestamp, "Event must be in future");

        bytes32 conditionId = generateConditionId(eventName, fighterA, fighterB, MarketType.GOES_DISTANCE);
        require(ufcMarkets[conditionId].conditionId == bytes32(0), "Market already exists");

        marketNonce++;

        string[] memory labels = new string[](2);
        labels[0] = "Yes - Goes to Decision";
        labels[1] = "No - Finish";

        conditionalTokens.createMarket(conditionId, collateralToken, 2, eventUri);

        ufcMarkets[conditionId] = UFCMarket({
            conditionId: conditionId,
            eventName: eventName,
            fighterA: fighterA,
            fighterB: fighterB,
            marketType: MarketType.GOES_DISTANCE,
            eventDate: eventDate,
            active: true,
            outcomeLabels: labels,
            collateralToken: collateralToken
        });

        allMarkets.push(conditionId);

        emit UFCMarketCreated(conditionId, eventName, fighterA, fighterB, MarketType.GOES_DISTANCE, labels);

        return conditionId;
    }

    function setCollateralApproval(address token, bool approved) external onlyOwner {
        approvedCollateral[token] = approved;
        emit CollateralApproved(token, approved);
    }

    function deactivateMarket(bytes32 conditionId) external onlyOwner {
        require(ufcMarkets[conditionId].conditionId != bytes32(0), "Market does not exist");
        ufcMarkets[conditionId].active = false;
        emit MarketDeactivated(conditionId);
    }

    function getOutcomeLabels(bytes32 conditionId) external view returns (string[] memory) {
        return ufcMarkets[conditionId].outcomeLabels;
    }

    function getAllMarkets() external view returns (bytes32[] memory) {
        return allMarkets;
    }

    function getActiveMarketsCount() external view returns (uint256 count) {
        for (uint256 i = 0; i < allMarkets.length; i++) {
            if (ufcMarkets[allMarkets[i]].active) count++;
        }
    }
}

